mapper.select:


SELECT REPLY_NUMBER, BOARD_BNO, REPLY_CONTENT, REPLY_WRITER, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE
FROM
    (
        SELECT /*+ INDEX_DESC(TBL_REPLY PK_REPLY) */
        ROWNUM RN, REPLY_NUMBER, BOARD_BNO, REPLY_CONTENT, REPLY_WRITER, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE
        FROM TBL_REPLY
        WHERE BOARD_BNO = #{boardBno} AND ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
    )
WHERE RN > (#{criteria.pageNum} -1) * #{criteria.amount}
